import{_ as t,o as e,c as o,Q as n}from"./chunks/framework.1a91c06a.js";const h=JSON.parse('{"title":"Enable Prometheus metrics","description":"","frontmatter":{"id":"enable-metrics","title":"Enable Prometheus metrics","head":[["meta",{"property":"og:title","content":"Enable Prometheus metrics | Lyfebloc Docs"}],["meta",{"property":"og:description","content":"Lyfebloc Network is a highly scalable modular blockchain powering dynamic applications with extensive full-stack adaptability."}]]},"headers":[],"relativePath":"get-started/metrics.md","filePath":"get-started/metrics.md","lastUpdated":1739729557000}'),r={name:"get-started/metrics.md"},d=n('<h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Lyfebloc Network can report and serve the Prometheus metrics, which in their turn can be consumed using Prometheus collector(s).</p><p>Prometheus metrics are disabled by default. It can be enabled by specifying the listener address using <code>--prometheus</code> flag or <code>Telemetry.prometheus</code> field in the config file. Metrics will be served under <code>/metrics</code> on the specified address.</p><h2 id="available-metrics" tabindex="-1">Available metrics <a class="header-anchor" href="#available-metrics" aria-label="Permalink to &quot;Available metrics&quot;">​</a></h2><p>The following metrics are available:</p><table><thead><tr><th><strong>Name</strong></th><th><strong>Type</strong></th><th><strong>Description</strong></th></tr></thead><tbody><tr><td>txpool_pending_transactions</td><td>Gauge</td><td>Number of pending transactions in TxPool</td></tr><tr><td>consensus_validators</td><td>Gauge</td><td>Number of Validators</td></tr><tr><td>consensus_rounds</td><td>Gauge</td><td>Number of Rounds</td></tr><tr><td>consensus_num_txs</td><td>Gauge</td><td>Number of Transactions in the latest block</td></tr><tr><td>consensus_block_interval</td><td>Gauge</td><td>Time between this and last block in seconds</td></tr><tr><td>network_peers</td><td>Gauge</td><td>Number of Connected peers</td></tr><tr><td>network_outbound_connections_count</td><td>Gauge</td><td>Number of outbound connections</td></tr><tr><td>network_inbound_connections_count</td><td>Gauge</td><td>Number of inbound connections</td></tr><tr><td>network_pending_outbound_connections_count</td><td>Gauge</td><td>Number of pending outbound connections</td></tr><tr><td>network_pending_inbound_connections_count</td><td>Gauge</td><td>Number of pending inbound connections</td></tr></tbody></table>',6),a=[d];function s(i,c,l,u,m,b){return e(),o("div",null,a)}const p=t(r,[["render",s]]);export{h as __pageData,p as default};
